mapper.insert:

	 	SELECT SEQ_USER.NEXTVAL FROM DUAL

INSERT INTO TB_USER(
	USER_ID
	,LOGIN_ID
	,LOGIN_PW
	,NAME
	,PHONE
	,EMAIL
	)VALUES(
	#{userId}
	,#{loginId}
	,#{loginPw}
	,#{name}
	,#{phone}
	,#{email}
	)

mapper.select:
SELECT COUNT(*) AS CNT
FROM TB_USER
WHERE LOGIN_ID=#{loginId}

SELECT COUNT(*) AS CNT
FROM TB_USER
WHERE PHONE=#{phone}

SELECT COUNT(*) AS CNT
FROM TB_USER
WHERE EMAIL=#{email}

SELECT LOGIN_ID
FROM TB_USER
WHERE NAME=#{name} AND PHONE=#{phone}

mapper.update:
UPDATE TB_USER
SET LOGIN_PW=#{loginPw}
WHERE LOGIN_ID=#{loginId} AND PHONE=#{phone}

mapper.select:
SELECT COUNT(*)
FROM TB_USER
WHERE LOGIN_ID=#{loginId} AND PHONE=#{phone}

mapper.update:
UPDATE TB_USER
SET 
NAME=#{name}
,PHONE=#{phone}
,EMAIL=#{email}

,LOGIN_PW=#{loginPw}

WHERE USER_ID=#{userId}
